    public boolean addNew(Juego juego){
        // Create and/or open the database for writing
        SQLiteDatabase db = getWritableDatabase();

        // It's a good idea to wrap our insert in a transaction. This helps with performance and ensures
        // consistency of the database.
        db.beginTransaction();
        try {
            ContentValues values = new ContentValues();
            if (juego.getImage()!= null){
                values.put("image", juego.getImage());
            }
            if (juego.getName() != null){
                values.put("name", juego.getName());
            }else {
                return false;
            }
            if (juego.getCompany() != null){
                values.put("company", juego.getCompany());
            }else {
                return false;
            }
            if (juego.getHours() > 0){
                values.put("hours", juego.getHours());
            }else {
                return false;
            }
            if (juego.getDate() != null){
                values.put("output_date", juego.getDate());
            }else {
                return false;
            }
            if (juego.getPlatform() != null){
                values.put("platform", juego.getPlatform());
            }else {
                return false;
            }
            values.put("property", juego.isProperty());

            // Notice how we haven't specified the primary key. SQLite auto increments the primary key column.
            db.insertOrThrow("Juego", null, values);
            db.setTransactionSuccessful();
            return  true;
        } catch (Exception e) {
            Log.d("DB", "Error while trying to add post to database");
            return false;
        } finally {
            db.endTransaction();
            db.close();
        }
    }


    public boolean modifyGame(Juego juego){
        SQLiteDatabase db = getWritableDatabase();
        String[] args = new String[]{String.valueOf(juego.getId())};
        db.beginTransaction();
        try {
            ContentValues values = new ContentValues();
            if (juego.getImage()!= null){
                values.put("image", juego.getImage());
            }
            //values.put("id",juego.getId());
            values.put("name", juego.getName());
            values.put("company", juego.getCompany());
            values.put("hours", juego.getHours());
            values.put("output_date", juego.getDate());
            values.put("platform", juego.getPlatform());
            values.put("property", juego.isProperty());

            db.update("Juego", values,
                    "id=?",args);

            db.setTransactionSuccessful();
            return  true;
        } catch (Exception e) {
            Log.d("DB", "Error while trying to add post to database");
            return false;
        } finally {
            db.endTransaction();
            db.close();
        }
    }

    public Juego getGame(int id){
        Juego juego = new Juego();
        String[] args = new String[]{String.valueOf(id)};
        SQLiteDatabase db = getWritableDatabase();
        Cursor c = db.rawQuery(" SELECT id,name,company,hours,output_date," +
                "platform,property,image FROM juego WHERE id=?", args);
        if (c.moveToFirst()) {
            juego.setId(c.getInt(0));
            juego.setName(c.getString(1));
            juego.setCompany(c.getString(2));
            juego.setHours(c.getInt(3));
            juego.setDate(c.getString(4));
            juego.setPlatform(c.getString(5));
            if (c.getInt(6) == 1) {
                juego.setProperty(true);
            } else {
                juego.setProperty(false);
            }
            if (c.getString(7) != null) {
                juego.setImage(c.getString(7));
            }
        }
        db.close();
        return juego;
    }

    public boolean deleteGame(int id){
        SQLiteDatabase db = getWritableDatabase();
        if (id>0){
            String[] args = new String[]{String.valueOf(id)};
            db.execSQL("DELETE FROM Juego WHERE id=?", args);
            db.close();
            return true;
        }else{
            db.close();
            return false;
        }
    }

    public void deleteAll(){
        ArrayList<Juego> juegos = getAllGames();
        for (int i=0; i<juegos.size();i++){
            deleteGame(juegos.get(i).getId());
        }
    }

