    public ArrayList<String> getAllPlatforms(){
        ArrayList<String> platforms = new ArrayList<>();
        SQLiteDatabase db = getWritableDatabase();
        Cursor c = db.rawQuery(" SELECT name FROM plataforma ", null);
        if (c.moveToFirst()){
            do {
                platforms.add(c.getString(0));
            }while (c.moveToNext());
        }
        db.close();
        return platforms;
    }

    public boolean addPlatform(String plat){
        SQLiteDatabase db = getWritableDatabase();
        db.beginTransaction();
        try {
            ContentValues values = new ContentValues();
            values.put("name", plat);
            db.insertOrThrow("Plataforma", null, values);
            db.setTransactionSuccessful();
            return  true;
        } catch (Exception e) {
            Log.d("DB", "Error while trying to add post to database");
            return false;
        } finally {
            db.endTransaction();
            db.close();
        }
    }

